<template>
  <div class="vux-uploader">
    <div class="vux-uploader_hd">
      <p class="vux-uploader_title">{{ title }}</p>
      <div class="vux-uploader_info">{{ fileList.length }} / {{ max }}</div>
    </div>
    <div class="vux-uploader_bd">
      <ul class="vux-uploader_files">
        <li class="vux-uploader_file" v-for="(item, index) in fileList" :key="index" :style="{
          backgroundImage: `url(${item})`
        }" @click="handleFileClick(item)">
        </li>
      </ul>
      <div class="vux-uploader_input-box" v-show="files.length < max">
        <input class="vux-uploader_input" ref="input" type="file" accept="image/*" :capture="capture" @change="change"/>
      </div>
    </div>
    <div class="vux-uploader_previewer" id="previewer" @click="hidePreviewer()">
      <span class="vux-uploader_preview-img" id="previewerImg"></span>
    </div>
  </div>
</template>
<script>
export default {
  name: "Uploader",
  props: {
    title: {
      type: String,
      default: "图片上传"
    },
    files: {
      type: Array,
      default: []
    },
    max: {
      type: Number | String,
      default: 5
    },
    capture: {
      type: Boolean | String,
      default: "camera"
    },
    compress: {
      type: Boolean,
      default: true
    },
    maxWidth: {
      type: String | Number,
      default: 500
    }
  },
  data() {
    return {
      fileList: []
    };
  },
  mounted() {
    this.fileList = this.fileList.concat(this.files);
    this.hidePreviewer();
  },
  methods: {
    change(e) {
      const target = e.target || e.srcElement;
      const file = target.files[0];
      this.readFile(file);
    },
    readFile(file) {
      const { compress, compressImage, fileList } = this;
      let reader;
      if (typeof FileReader !== "undefined") {
        reader = new FileReader();
      } else if (window.FileReader) {
        reader = new window.FileReader();
      } else {
        alert("your browser are not support FileReader");
      }
      reader.onload = e => {
        const rawDataUrl = e.target.result;
        if (compress) {
          compressImage(rawDataUrl, dataUrl => {
            fileList.push(dataUrl);
          });
        } else {
          fileList.push(rawDataUrl);
        }
      };
      reader.readAsDataURL(file);
    },
    compressImage(dataUrl, cb) {
      const image = new Image();
      image.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        let w = Math.min(this.maxWidth, image.width);
        let h = image.height * (w / image.width);
        canvas.width = w;
        canvas.height = h;
        ctx.clearRect(0, 0, w, h);
        ctx.drawImage(image, 0, 0, w, h);
        const result = canvas.toDataURL("image/png");
        const rate = w / image.width * 100;
        console.log("compress rate", rate.toFixed(2) + "%");
        cb(result);
      };
      image.src = dataUrl;
    },
    handleFileClick(item) {
      console.log(item);
      this.showPreviewer();
      const previewerImg = document.getElementById("previewerImg");
      previewerImg.style.backgroundImage = `url(${item})`;
    },
    showPreviewer() {
      const previewer = document.getElementById("previewer");
      if (previewer) {
        previewer.style.display = "block";
        previewer.style.opacity = 1;
      }
    },
    hidePreviewer() {
      const previewer = document.getElementById("previewer");
      if (previewer) {
        previewer.style.display = "none";
        previewer.style.opacity = 0;
      }
    }
  }
};
</script>
